import {createSelector} from 'reselect'

const ID={
    hats:1,
    sneakers:2,
    jackets:3,
    womens:4,
    mens:5,
}

const shopSelecter=(state)=>{return state.shop}

export const itemSel=createSelector([shopSelecter],(t)=>{
    console.log(t)
    return t.item})

// export const idsel=(para)=>
//     createSelector([itemSel],(item)=>
//         item.map((it)=>
//             (it.id===ID[para])
//         )
//     )


export const idsel=(para)=>createSelector([itemSel],item=>item[para])
