import {createSelector} from 'reselect'

const ID={
    hats:1,
    sneakers:2,
    jackets:3,
    womens:4,
    mens:5,
}

const shopSelecter=(state)=>{return state.shop}

export const itemSel=createSelector([shopSelecter],(t)=>{
    console.log(t)
    return t.collections})

export const selectCollection=para=>
createSelector(
    [itemSel],
    collections=>
    collections.find(
        i=>  i.id===ID[para]
    )
)
