import {createSelectors} from 'reselect';
const cartSelector =(state)=>{return (state.cart)}
export const cartItemSelector=createSelectors([cartSelector],(cart)=>{return (cartItem)})
export const itemCountSelector=createSelectors([cartItemSelector],
    (item)=>item.reduce((need,item)=>{return(need+item.count)},0)
    )
