import {createSelector} from 'reselect'

const ID={
    hats:1,
    sneakers:2,
    jackets:3,
    womens:4,
    mens:5,
}

const shopSelecter=(state)=>{return state.shop}

export const itemSel=createSelector([shopSelecter],(t)=>{
    console.log(t)
    return t.item})

export const idsel=(para)=>{createSelector([shopSelecter],(item)=>{
    item.find((i)=>{
        console.log("vvvvvvvvvvvvvvvvvvvv")
        console.log(i)
      return  i.id===ID[para]
    })
})}
