import cartType from './cart.types'

const INITIAL_STATE={
    hidden:true,
    cartItem:[]
}

const cartReducer=(state=INITIAL_STATE,action)=>{
    switch(action.type){
        case cartType.CART_TOGGLE:
            return{
                ...state,
                hidden:!(state.hidden)
            }
         case cartType.ADD_ITEM:
            return{
                ...state,
                cartItem:[...state.cartItem,action.payload]
            }
        default:
            return state
    }
}
export default cartReducer
